// SPDX-License-Identifier: MIT
pragma solidity >=0.8.0 <0.9.0;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title Escrow
 * @dev Contract for facilitating secure crypto-to-fiat trades with escrow and dispute resolution
 */
contract Escrow is ReentrancyGuard, Ownable {
    enum TradeState { INACTIVE, AWAITING_DEPOSITS, ACTIVE, FIAT_SENT, FIAT_RECEIVED, COMPLETED, DISPUTED, CANCELLED }
    enum DisputeDecision { RELEASE, CANCEL }
    enum PenaltyTarget { NONE, BUYER, SELLER }

    struct Trade {
        address payable buyer;
        address payable seller;
        address payable arbitrator;
        uint256 cryptoAmount;
        uint256 buyerCollateral;
        uint256 sellerCollateral;
        uint256 depositDeadline;
        uint256 confirmationDeadline;
        uint256 feeRate; // In basis points (1/100 of a percent, so 100 = 1%)
        address payable platformWallet;
        TradeState state;
        bool buyerDeposited;
        bool sellerDeposited;
    }

    Trade public currentTrade;
    bool private locked;
    uint256 public constant BASIS_POINTS_DIVISOR = 10000; // For fee calculations (100% = 10000)

    // Events
    event TradeInitialized(address buyer, address seller, address arbitrator, uint256 cryptoAmount, uint256 depositDeadline);
    event BuyerDeposited(uint256 amount, uint256 collateral);
    event SellerDeposited(uint256 amount, uint256 collateral);
    event FiatSendConfirmed(address confirmer);
    event FiatReceiveConfirmed(address confirmer);
    event TradeCompleted(address buyer, address seller, uint256 cryptoAmount);
    event TradeCancelled(address initiator, string reason);
    event DisputeRaised(address initiator);
    event DisputeResolved(DisputeDecision decision, PenaltyTarget penaltyTarget);
    event FeeUpdated(uint256 newFeeRate, address platformWallet);

    /**
     * @dev Constructor for initializing contract ownership
     */
    constructor() Ownable(msg.sender) {}

    /**
     * @dev Initialize a new trade with specified parameters
     * @param _buyer The address of the buyer
     * @param _seller The address of the seller
     * @param _arbitrator The address of the arbitrator
     * @param _cryptoAmount The amount of crypto to be traded
     * @param _buyerCollateral Collateral amount required from buyer
     * @param _sellerCollateral Collateral amount required from seller
     * @param _depositDuration Time in seconds allowed for deposits
     * @param _confirmationDuration Time in seconds allowed for confirmations
     * @param _feeRate Trading fee in basis points
     * @param _platformWallet Address to receive fees
     */
    function initTrade(
        address payable _buyer,
        address payable _seller,
        address payable _arbitrator,
        uint256 _cryptoAmount,
        uint256 _buyerCollateral,
        uint256 _sellerCollateral,
        uint256 _depositDuration,
        uint256 _confirmationDuration,
        uint256 _feeRate,
        address payable _platformWallet
    ) external onlyOwner {
        require(currentTrade.state == TradeState.INACTIVE || 
                currentTrade.state == TradeState.COMPLETED || 
                currentTrade.state == TradeState.CANCELLED, 
                "Current trade must be completed, cancelled, or inactive");
        require(_buyer != address(0) && _seller != address(0) && _arbitrator != address(0), "Invalid addresses");
        require(_buyer != _seller && _buyer != _arbitrator && _seller != _arbitrator, "Addresses must be different");
        require(_cryptoAmount > 0 && _buyerCollateral > 0 && _sellerCollateral > 0, "Amounts must be greater than zero");
        require(_depositDuration > 0 && _confirmationDuration > 0, "Durations must be greater than zero");
        require(_feeRate < BASIS_POINTS_DIVISOR, "Fee rate must be less than 100%");
        require(_platformWallet != address(0), "Invalid platform wallet");

        currentTrade = Trade({
            buyer: _buyer,
            seller: _seller,
            arbitrator: _arbitrator,
            cryptoAmount: _cryptoAmount,
            buyerCollateral: _buyerCollateral,
            sellerCollateral: _sellerCollateral,
            depositDeadline: block.timestamp + _depositDuration,
            confirmationDeadline: 0, // Will be set when deposits are completed
            feeRate: _feeRate,
            platformWallet: _platformWallet,
            state: TradeState.AWAITING_DEPOSITS,
            buyerDeposited: false,
            sellerDeposited: false
        });

        emit TradeInitialized(_buyer, _seller, _arbitrator, _cryptoAmount, currentTrade.depositDeadline);
    }

    /**
     * @dev Allow the buyer to deposit crypto and collateral
     */
    function buyerDeposit() external payable nonReentrant {
        require(currentTrade.state == TradeState.AWAITING_DEPOSITS, "Trade not in awaiting deposits state");
        require(msg.sender == currentTrade.buyer, "Only buyer can deposit");
        require(!currentTrade.buyerDeposited, "Buyer already deposited");
        require(block.timestamp <= currentTrade.depositDeadline, "Deposit deadline passed");
        require(msg.value == currentTrade.buyerCollateral, "Incorrect collateral amount");

        currentTrade.buyerDeposited = true;

        // Check if both parties have deposited and update state if needed
        if (currentTrade.sellerDeposited) {
            currentTrade.state = TradeState.ACTIVE;
            currentTrade.confirmationDeadline = block.timestamp + 3600; // 1 hour for confirmation
        }

        emit BuyerDeposited(0, msg.value);
    }

    /**
     * @dev Allow the seller to deposit crypto and collateral
     */
    function sellerDeposit() external payable nonReentrant {
        require(currentTrade.state == TradeState.AWAITING_DEPOSITS, "Trade not in awaiting deposits state");
        require(msg.sender == currentTrade.seller, "Only seller can deposit");
        require(!currentTrade.sellerDeposited, "Seller already deposited");
        require(block.timestamp <= currentTrade.depositDeadline, "Deposit deadline passed");
        require(msg.value == currentTrade.cryptoAmount + currentTrade.sellerCollateral, "Incorrect amount");

        currentTrade.sellerDeposited = true;

        // Check if both parties have deposited and update state if needed
        if (currentTrade.buyerDeposited) {
            currentTrade.state = TradeState.ACTIVE;
            currentTrade.confirmationDeadline = block.timestamp + 3600; // 1 hour for confirmation
        }

        emit SellerDeposited(currentTrade.cryptoAmount, currentTrade.sellerCollateral);
    }

    /**
     * @dev Allow the buyer to confirm they sent the fiat payment
     */
    function confirmFiatSent() external nonReentrant {
        require(currentTrade.state == TradeState.ACTIVE, "Trade not active");
        require(msg.sender == currentTrade.buyer, "Only buyer can confirm fiat sent");
        require(block.timestamp <= currentTrade.confirmationDeadline, "Confirmation deadline passed");

        currentTrade.state = TradeState.FIAT_SENT;

        emit FiatSendConfirmed(msg.sender);
    }

    /**
     * @dev Allow the seller to confirm they received the fiat payment
     */
    function confirmFiatReceived() external nonReentrant {
        require(currentTrade.state == TradeState.FIAT_SENT, "Fiat must be sent first");
        require(msg.sender == currentTrade.seller, "Only seller can confirm fiat received");
        require(block.timestamp <= currentTrade.confirmationDeadline, "Confirmation deadline passed");

        currentTrade.state = TradeState.FIAT_RECEIVED;

        emit FiatReceiveConfirmed(msg.sender);
    }

    /**
     * @dev Complete the trade by releasing crypto to buyer after fiat is received
     */
    function completeTrade() external nonReentrant {
        require(currentTrade.state == TradeState.FIAT_RECEIVED, "Fiat must be received first");
        require(msg.sender == currentTrade.seller, "Only seller can complete the trade");

        // Calculate fee
        uint256 fee = (currentTrade.cryptoAmount * currentTrade.feeRate) / BASIS_POINTS_DIVISOR;
        uint256 buyerAmount = currentTrade.cryptoAmount - fee;

        // Update state first to prevent reentrancy
        currentTrade.state = TradeState.COMPLETED;

        // Return collaterals
        currentTrade.buyer.transfer(currentTrade.buyerCollateral);
        currentTrade.seller.transfer(currentTrade.sellerCollateral);

        // Transfer crypto amount (minus fee) to buyer
        currentTrade.buyer.transfer(buyerAmount);

        // Transfer fee to platform wallet
        currentTrade.platformWallet.transfer(fee);

        emit TradeCompleted(currentTrade.buyer, currentTrade.seller, buyerAmount);
    }

    /**
     * @dev Cancel the trade if deadlines are missed
     */
    function cancelTrade() external nonReentrant {
        require(
            currentTrade.state == TradeState.AWAITING_DEPOSITS || 
            currentTrade.state == TradeState.ACTIVE || 
            currentTrade.state == TradeState.FIAT_SENT, 
            "Trade cannot be cancelled in current state"
        );

        require(
            msg.sender == currentTrade.buyer || 
            msg.sender == currentTrade.seller, 
            "Only buyer or seller can cancel"
        );

        // Check deadline conditions
        if (currentTrade.state == TradeState.AWAITING_DEPOSITS) {
            require(block.timestamp > currentTrade.depositDeadline, "Deposit deadline not passed");
        } else {
            require(block.timestamp > currentTrade.confirmationDeadline, "Confirmation deadline not passed");
        }

        // Update state first to prevent reentrancy
        currentTrade.state = TradeState.CANCELLED;

        // Refund deposits
        if (currentTrade.buyerDeposited) {
            currentTrade.buyer.transfer(currentTrade.buyerCollateral);
        }

        if (currentTrade.sellerDeposited) {
            currentTrade.seller.transfer(currentTrade.sellerCollateral + currentTrade.cryptoAmount);
        }

        emit TradeCancelled(msg.sender, "Deadline missed");
    }

    /**
     * @dev Raise a dispute for arbitration
     */
    function raiseDispute() external nonReentrant {
        require(
            currentTrade.state == TradeState.ACTIVE || 
            currentTrade.state == TradeState.FIAT_SENT || 
            currentTrade.state == TradeState.FIAT_RECEIVED, 
            "Trade cannot be disputed in current state"
        );

        require(
            msg.sender == currentTrade.buyer || 
            msg.sender == currentTrade.seller, 
            "Only buyer or seller can raise dispute"
        );

        require(block.timestamp > currentTrade.confirmationDeadline, "Confirmation deadline not passed");

        currentTrade.state = TradeState.DISPUTED;

        emit DisputeRaised(msg.sender);
    }

    /**
     * @dev Resolve a dispute by the arbitrator
     * @param decision Whether to release or cancel the trade
     * @param penaltyTarget Which party should be penalized, if any
     */
    function resolveDispute(DisputeDecision decision, PenaltyTarget penaltyTarget) external nonReentrant {
        require(currentTrade.state == TradeState.DISPUTED, "Trade not in disputed state");
        require(msg.sender == currentTrade.arbitrator, "Only arbitrator can resolve dispute");
        require(penaltyTarget != PenaltyTarget.NONE || decision == DisputeDecision.CANCEL, "Penalty required for release decision");

        // Update state first to prevent reentrancy
        currentTrade.state = decision == DisputeDecision.RELEASE ? TradeState.COMPLETED : TradeState.CANCELLED;

        if (decision == DisputeDecision.RELEASE) {
            // Calculate fee
            uint256 fee = (currentTrade.cryptoAmount * currentTrade.feeRate) / BASIS_POINTS_DIVISOR;
            uint256 buyerAmount = currentTrade.cryptoAmount - fee;

            // Apply penalty if necessary
            if (penaltyTarget == PenaltyTarget.BUYER) {
                // Buyer penalized - seller gets buyer's collateral
                currentTrade.seller.transfer(currentTrade.sellerCollateral + currentTrade.buyerCollateral);
                currentTrade.buyer.transfer(buyerAmount);
            } else if (penaltyTarget == PenaltyTarget.SELLER) {
                // Seller penalized - buyer gets seller's collateral
                currentTrade.buyer.transfer(currentTrade.buyerCollateral + currentTrade.sellerCollateral + buyerAmount);
            }

            // Transfer fee to platform wallet
            currentTrade.platformWallet.transfer(fee);

        } else {
            // Cancel the trade
            if (penaltyTarget == PenaltyTarget.BUYER) {
                // Buyer penalized - seller gets buyer's collateral
                currentTrade.seller.transfer(currentTrade.sellerCollateral + currentTrade.buyerCollateral + currentTrade.cryptoAmount);
            } else if (penaltyTarget == PenaltyTarget.SELLER) {
                // Seller penalized - buyer gets seller's collateral and crypto
                currentTrade.buyer.transfer(currentTrade.buyerCollateral + currentTrade.sellerCollateral + currentTrade.cryptoAmount);
            } else {
                // No penalty - return all funds
                currentTrade.buyer.transfer(currentTrade.buyerCollateral);
                currentTrade.seller.transfer(currentTrade.sellerCollateral + currentTrade.cryptoAmount);
            }
        }

        emit DisputeResolved(decision, penaltyTarget);
    }

    /**
     * @dev Update platform fee and wallet
     * @param _newFeeRate New fee rate in basis points
     * @param _newPlatformWallet New wallet to receive fees
     */
    function updateFeeSettings(uint256 _newFeeRate, address payable _newPlatformWallet) external onlyOwner {
        require(_newFeeRate < BASIS_POINTS_DIVISOR, "Fee rate must be less than 100%");
        require(_newPlatformWallet != address(0), "Invalid platform wallet");

        currentTrade.feeRate = _newFeeRate;
        currentTrade.platformWallet = _newPlatformWallet;

        emit FeeUpdated(_newFeeRate, _newPlatformWallet);
    }

    /**
     * @dev Get current trade state
     */
    function getTradeState() external view returns (TradeState) {
        return currentTrade.state;
    }

    /**
     * @dev Get trade details
     */
    function getTradeDetails() external view returns (
        address, address, address, uint256, uint256, uint256, uint256, uint256, TradeState
    ) {
        return (
            currentTrade.buyer,
            currentTrade.seller,
            currentTrade.arbitrator,
            currentTrade.cryptoAmount,
            currentTrade.buyerCollateral,
            currentTrade.sellerCollateral,
            currentTrade.depositDeadline,
            currentTrade.confirmationDeadline,
            currentTrade.state
        );
    }
}

